function result_temp_d = rotate_block(temp_d,h)%         d=X(k:(k-1)+d_block,l:(l-1)+d_block);%         temp_d=imresize(d,0.5);                        if h==1                            result_temp_d=temp_d;                        elseif h==2                            result_temp_d=imrotate(temp_d,90,'bilinear');                        elseif h==3                            result_temp_d=imrotate(temp_d,180,'bilinear');                        elseif h==4                            result_temp_d=imrotate(temp_d,270,'bilinear');                        elseif h==5                            tform=maketform('affine',[-1 0 0; 0 1 0; 0 0 1]);                            result_temp_d=imtransform(temp_d,tform);                        elseif h==6                            tform=maketform('affine',[1 0 0; 0 -1 0; 0 0 1]);                            result_temp_d=imtransform(temp_d,tform);                        elseif h==7                            tform=maketform('affine',[0 1 0; 1 0 0; 0 0 1]);                            result_temp_d=imtransform(temp_d,tform);                        elseif h==8                            tform=maketform('affine',[-1 0 0; 0 -1 0; 0 0 1]);                            result_temp_d=imtransform(temp_d,tform);                        endend